mapper.select:
SELECT id, DATE_FORMAT(startdate, '%Y-%m-%d') AS startdate, DATE_FORMAT(enddate, '%Y-%m-%d') AS enddate, used
FROM water
WHERE id = #{id}

SELECT id, filter_no, DATE_FORMAT(regtime, '%Y-%m-%d %H:%i:%s') AS regtime
FROM water_status
WHERE filter_no = #{id}

mapper.update:
UPDATE water
SET enddate = NOW()
WHERE id = #{id}
